#include <stdio.h>#include <vector>using  namespace std;vector<int> vec;int main(){    int poly,e;    while( scanf("%d %d",&poly, &e) !=EOF){        vec.push_back(poly);        vec.push_back(e);  //poly for expansion , e for exp    }    vector<int>::iterator it = vec.begin(); //fist one factor    int length = vec.size();    int count =0;    while(length > 0) {        poly = (*it) * (*(it + 1));        e = (*(it + 1)) - 1;        if (poly != 0)        {            printf("%d %d",poly,e);            if(length>2) printf(" ");            count++;        }        it +=2;        length -=2;    }    if(count ==0) printf("0 0"); //all constant    return  0;}